import "../styles/components/Forecast.scss";
function Forecast({ title, type, data }) {
  return (
    <div className="Forecast">
      <div className="forecast-container">
        <h3>{title}</h3>
        <div className="widget-container">
          {data.map((singleData) => (
            <div>
              {type === "hourly" ? (
                <HourlyForecastWidget />
              ) : (
                <DailyForecastWidget />
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Forecast;
